templates.template:
tabris.create("CheckBox", {
  text: "${text}",
  selection: ${false},
  layoutData: { left: ${left}, top: ${top} }
})${cursor}

tabris.create("Combo", {
  items: ["${item1}", "${item2}"],
  text: "${text}",
  selectionIndex: ${number},
  layoutData: { left: ${left}, top: ${top} }
})${cursor}

tabris.create("ImageView", {
  image : {
    src : "${path}"
  },
  scaleMode: "${fill}",
  layoutData: { left: ${left}, top: ${top} }
})${cursor}

tabris.create("Label", {
  text: "${text}",
  font: "${font}",
  layoutData: { left: ${left}, top: ${top} }
})${cursor}

tabris.create("List", {
  items: [${items}],
  itemHeight: ${number},
  linesVisible: ${false},
  layoutData: { left: ${left}, top: ${top} }
})${cursor}

tabris.create("ProgressBar", {
  minimum: ${0},
  maximum: ${100},
  selection: ${0},
  state: "${normal}",
  layoutData: { left: ${left}, top: ${top} }
})${cursor}

tabris.create("RadioButton", {
  text: "${text}",
  selection: ${false},
  layoutData: { left: ${left}, top: ${top} }
})${cursor}

tabris.create("Slider", {
  minimum: ${0},
  maximum: ${100},
  selection: ${0},
  layoutData: { left: ${left}, top: ${top} }
})${cursor}

tabris.create("TabFolder", {
  paging: ${false},
  layoutData: { left: ${left}, top: ${top}, height:${height} }
})${cursor}

tabris.create("Text", {
  text: "${text}",
  editable: ${boolean},
  type: "${default}",
  layoutData: { left: ${left}, top: ${top} }
})${cursor}

tabris.create("ToggleButton", {
  text: "${text}",
  selection: ${false},
  layoutData: { left: ${left}, top: ${top} }
})${cursor}
